#!/bin/bash

alias pull-git='find -name .git -type d -printf "\033[0;33m%h:\033[0m\n" -execdir bash -c "MAIN=\$([ -f .git/refs/heads/main ] && echo main || echo master) && git fetch --quiet origin && git --no-pager diff --color=always --stat \${MAIN} origin/\${MAIN} && git update-ref refs/heads/\${MAIN} origin/\${MAIN} && if [[ \$(git symbolic-ref --short HEAD) == \${MAIN} ]] ; then git reset --hard HEAD ; fi" \;'
alias pull-git-parallel=$'find -name .git -type d -printf "%h\\0" | xargs -0 -P 0 -I {} bash -c "(cd {} && MAIN=\$([ -f .git/refs/heads/main ] && echo main || echo master) && git fetch --quiet origin && git --no-pager diff --color=always --stat \${MAIN} origin/\${MAIN} && git update-ref refs/heads/\${MAIN} origin/\${MAIN} && if [[ \$(git symbolic-ref --short HEAD) == \${MAIN} ]] ; then git reset --hard HEAD ; fi) > >(awk \'\$0=\\"\x1b[0;33m{}: \x1b[0m\\"\$0\') 2> >(awk \'\$0=\\"\x1b[0;31m{}: \x1b[0m\\"\$0\' >&2)"'
